mapper.select:

SELECT EXISTS (
    SELECT 1
    FROM USERS
    WHERE login_id = #{loginId}
)


SELECT EXISTS (
    SELECT 1
    FROM USERS
    WHERE email = #{email}
)

mapper.insert:
INSERT INTO USERS (
    login_id, password, email, name, login_type, enabled, use_yn, delete_yn, updated_at
) VALUES (
    #{loginId}, #{password}, #{email}, #{name}, #{loginType}, #{enabled}, #{useYn}, #{deleteYn}, NOW()
)

INSERT INTO USER_ROLES (
    user_id, role, use_yn, delete_yn, updated_at
) VALUES (
    #{userId}, #{role}, #{useYn}, #{deleteYn}, NOW()
)
